	public class DuplicateZeros {

    public static void main(String[] args) {
        int[] arr = {1, 0, 2, 3, 0, 4, 5, 0};
        duplicateZerosApproach3Optimal(arr);
        System.out.println("");
    }

    // TC: O(N^2) SC: O(1)
    private static void duplicateZerosApproach1(int[] arr) {
        for (int i = 0; i < arr.length; i++) {
            if (arr[i] == 0) {
                shiftRight(arr, i);
                i++;
            }
        }
    }

    private static void shiftRight(int[] arr, int i) {
        for (int j = arr.length - 1; j > i; j--) {
            arr[j] = arr[j - 1];
        }
    }

    // TC: O(N) SC: O(N)
    private static void duplicateZerosApproach2UsingQueue(int[] arr) {
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < arr.length; i++) {
            queue.add(arr[i]);
            if (arr[i] == 0) {
                queue.add(0);
            }
            arr[i] = queue.remove();
        }
    }


    // TC: O(N) SC: O(1)
    private static void duplicateZerosApproach3Optimal(int[] arr) {
        int count =0;
        for(int i=0;i<arr.length;i++){
            if(arr[i]==0){
                count++;
            }
        }
        int i=arr.length-1, j=arr.length+count-1;
        while(i!=j){
            insert(arr,i,j--);
            if(arr[i]==0){
                insert(arr,i,j--);
            }
            i--;
        }

    }

    private static void insert(int[] arr, int i, int j) {
        if(j<arr.length){
            arr[j]=arr[i];
        }
    }


}

