class Solution {
    public void duplicateZeros(int[] arr) {
        int zeroCount = 0;
        int n = arr.length;

        //if input = [1,0,2,3,0,4,5,0]
            //zeroCount = 3
        for (int i = 0; i < n; i++) {
            if (arr[i] == 0) {
                zeroCount++;
            }
        }

        // temptArray size = n + 3
        int[] temptArray = new int[n + zeroCount];
        int index = 0;

        //temptArray = [1,0,0,2,3,0,0,4,5,0,0]
        for (int i = 0; i < n; i++) {
            if (arr[i] == 0) {
                temptArray[++index] = 0;
            }
            else {
                temptArray[index] = arr[i];
            }

            index++;
        }

        // arr = [1,0,0,2,3,0,0,4]  won't add in [5, 0, 0]
        for (int i = 0; i < n; i++) {
            arr[i] = temptArray[i];
        }

        //Time: O(n)
        //Space: O(n)
    }
}
