class Solution:
def duplicateZeros(self, arr: [int]) -> None:
        n = len(arr)
        zeros = arr.count(0)

        i = n - 1

        while i >= 0:
            if i + zeros < n:
                arr[i + zeros] = arr[i]
                if arr[i] == 0 and i + zeros - 1 >= 0:
                    zeros -= 1
                    arr[i + zeros] = 0
            elif arr[i] == 0:
                zeros -= 1
            else:
                arr[i] = 0
            i -= 1

        return arr
