    def numTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        def is_perf_sqrt(n): 
            """
            returns bool if a number is a perfect square
            like 4, 16, 25 --> True
            """
            return int(math.sqrt(n) + 0.5) ** 2 == n

        nums1_cntr, nums2_cntr = Counter(nums1), Counter(nums2)

