class Solution {
public:
    bool perfectSquare(long long int n) {
        long long int ans = sqrt(n);
        return n == ans*ans;
    }

    int squareRoot(long long int n) {
        int ans = sqrt(n);
        return ans;
    }

    int findTriplets(vector<int> a, vector<int> b) {
        int ans = 0;
        unordered_map<int, int> frequency;
        for (auto &y: b) {
            frequency[y]++;
        }
        int n = a.size();
        for (int i=0; i<n-1; i++) { 
            for (int j=i+1; j<n; j++) {
                long long int num = a[i];
                num *= a[j];
                if (perfectSquare(num)) {
                    ans += frequency[squareRoot(num)];
                }
            }
        }
        return ans;
    }

    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        return findTriplets(nums1, nums2) + findTriplets(nums2, nums1);
    }
};
