class Solution {
public:
    int twoProduct(long target, vector<int> &nums) {
        unordered_map<int, int> mp;
        int count = 0;

        for(int &x : nums) {
            if(target%x == 0) {
                count += mp[target/x];
            }
            mp[x]++;
        }
        return count;
    }

    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        auto lambda1 = [&](int s, long n) {
            return s + twoProduct(n*n, nums2);
        };

        auto lambda2 = [&](int s, long n) {
            return s + twoProduct(n*n, nums1);
        };

        return accumulate(begin(nums1), end(nums1), 0, lambda1) +
               accumulate(begin(nums2), end(nums2), 0, lambda2);
    }
};
