            a = {}
            for i in nums1:
                if i not in a:
                    a[i] = [i**2, 1]
                else:
                    a[i][-1] += 1

            # Initialize a dict: nums2[i] * nums[j] -> count
            b = collections.defaultdict(int)
            for i in range(len(nums2)):
