class Solution {
public:
    int numTriplets(vector<int>& n1, vector<int>& n2) {
        int n=n1.size();int m=n2.size();
        long ans=0;
        //type 1
        for(int i=0;i<n;i++)
        {
            double a=n1[i];
            unordered_map<double,int> s;
            for(int j=0;j<m;j++)
            {
                double b=n2[j];
                double f=a*a/b;
                if(s.find(f)!=s.end()) ans+=s[f];
                s[b]++;
            }
        }
        //type 2
        for(int i=0;i<m;i++)
        {
            double a=n2[i];
            unordered_map<double,int> s;
            for(int j=0;j<n;j++)
            {
                double b=n1[j];
                double f=a*a/b;
                if(s.find(f)!=s.end()) ans+=s[f];
                s[b]++;
            }
        }
        return ans;
    }
};
