    def numTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        Count1 = Counter(nums1)
        Count2 = Counter(nums2)
        res = 0
        for i in Count1:
            db = Count2.copy()
            while db:
                item = db.popitem()
                Q, r = divmod(i*i, item[0])
                if r == 0 and Q in db:
                    res += db[Q]*item[1]*Count1[i]
                    db.pop(Q)
                elif r == 0 and Q == item[0]:
