    def numTriplets(self, nums1, nums2):
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :rtype: int
        """     
        square1 = map(lambda x : x* x, nums1)
        square2 = map(lambda x : x* x, nums2)
        #frequency of nums1^2 and nums2^2
        freq1, freq2 = defaultdict(int),defaultdict(int)
        m1, m2 = range(len(nums1)), range(len(nums2))
        for i in m1:
            freq1[square1[i]] += 1
        for i in m2:
            freq2[square2[i]] += 1

        Ans = 0
        #type1
        for item in freq1:
            for j in m2:
                if item % nums2[j] == 0: 
                    k = item / nums2[j]
                    Ans += nums2[j+1:].count(k) * freq1[item]
        #type2
        for item in freq2:
            for j in m1:
                if item % nums1[j] == 0:
                    k = item / nums1[j]
                    Ans += nums1[j+1:].count(k) * freq2[item]

        return Ans
