/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var numTriplets = function(nums1, nums2) {
  var res = 0
  nums1.sort((a, b) => a - b < 0 ? -1 : 1)
  nums2.sort((a, b) => a - b < 0 ? -1 : 1)

  function process(nums1, nums2) {
    var len1 = nums1.length
    var len2 = nums2.length
    for (var i = 0; i < len1; i++) {
      var leftPointer = 0
      var RightPointer = len2 - 1
      var cur = nums1[i]
      var curSqr = Math.pow(cur, 2)

      while (leftPointer < RightPointer) {
        var curMul = nums2[leftPointer] * nums2[RightPointer]
        if (curMul > curSqr) {
          RightPointer--
        } else if (curMul < curSqr) {
          leftPointer++
        } else {
          if (nums2[leftPointer] === nums2[RightPointer]) {
            let len = RightPointer - leftPointer + 1
            res += (len * (len - 1)) / 2
            break
          } else {
            let right = nums2[RightPointer]
            let left = nums2[leftPointer]
            let leftSameCount = 0
            let rightSameCount = 0
            while (right === nums2[RightPointer]) {
              RightPointer--
              rightSameCount++
            }
            while (left === nums2[leftPointer]) {
              leftPointer++
              leftSameCount++
            }
            res += leftSameCount * rightSameCount
          }
        }
      }
    }
  }  

  process(nums1, nums2)
  process(nums2, nums1)

  return res
};
