class Solution:
    def numTriplets(self, nums1, nums2):
        # build hash table for both nums1 and nums2
        num1_sq_dict = collections.defaultdict(int)
        num2_sq_dict = collections.defaultdict(int)

        for n in nums1:
            num1_sq_dict[n * n] += 1
        for n in nums2:
            num2_sq_dict[n * n] += 1

        ans = 0

        len_n1 = len(nums1)
        len_n2 = len(nums2)
        for i in range(len_n1 - 1):
            for j in range(i + 1, len_n1):
                prod = nums1[i] * nums1[j]
                if prod in num2_sq_dict:
                    ans += num2_sq_dict[prod]
        for i in range(len_n2 - 1):
            for j in range(i + 1, len_n2):
                prod = nums2[i] * nums2[j]
                if prod in num1_sq_dict:
                    ans += num1_sq_dict[prod]

        return ans
