use std::collections::*;

impl Solution {
  pub fn num_triplets(nums1: Vec<i32>, nums2: Vec<i32>) -> i32 {
      let nums1 = nums1.into_iter().map(|v| v as usize).collect::<Vec<usize>>();
      let nums2 = nums2.into_iter().map(|v| v as usize).collect::<Vec<usize>>();
      let mut m1 = HashMap::new();
      let mut m2 = HashMap::new();
      let n1 = nums1.len();
      let n2 = nums2.len();

      for i in 0..n1 {
        for j in i+1..n1 {
          *m1.entry(nums1[i] * nums1[j]).or_insert(0) += 1;
        }
      }
      for i in 0..n2 {
        for j in i+1..n2 {
          *m2.entry(nums2[i] * nums2[j]).or_insert(0) += 1;
        }
      }

      let mut result = 0;
      for v in nums1 {
        if let Some(num) = m2.get(&(v*v)) {
          result += num;
        }
      }
      for v in nums2 {
        if let Some(num) = m1.get(&(v*v)) {
          result += num;
        }
      }
      result as i32
  }
}
