typedef long long ll;
class Solution {
public:
    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        unordered_map<ll,int> m1, m2;
        for(auto i:nums1) m1[i]++;
        for(auto i:nums2) m2[i]++;
        int ans1 = 0,ans2 = 0;
        for(auto i:m1){
            ll x1 = i.first*i.first;
            int y1 = i.second;
            for(auto j:m2){
                int x2 = j.first, y2 = j.second;
                if(x1%x2 == 0){
                    if(x1/x2 == x2) ans1 += y1*(y2*(y2-1))/2;
                    else if(m2.find(x1/x2) != m2.end()) ans2 += y1*y2*m2[x1/x2];
                }
            }
            // cout<<i.first<<" -> "<<ans1<<" ";
        }
        for(auto i:m2){
            ll x1 = i.first*i.first;
            int y1 = i.second;
            for(auto j:m1){
                int x2 = j.first, y2 = j.second;
                if(x1%x2 == 0){
                    if(x1/x2 == x2) ans1 += y1*(y2*(y2-1))/2;
                    else if(m1.find(x1/x2) != m1.end()) ans2 += y1*y2*m1[x1/x2];
                }
            }
            // cout<<i.first<<" -> "<<ans1<<" ";
        }
        return ans1 + ans2/2;
    }
};
