                    if(hash2[b % n2][1] == 0){
                        hash2[b % n2][0] = nums2[j];
                        hash2[b % n2][1] = 1 ;
                        break;
                    }                    
                    if(hash2[b % n2][0] == nums2[j]){
                        hash2[b % n2][1]++;
                        break;
                    }
                    b++;
                }
            }
        }

        //insert nums1 into hash1 table
        hash1[a % n1][0] = nums1[i];
        hash1[a % n1][1] = cn;

        next:
        ans += cn;

    }

    return ans;
}

