var numTriplets = function(nums1, nums2) {
    // construct two empty dictionaries to store all the multiplication numbers
    const nums1Dict = {};
    const nums2Dict = {};

    // Go over the first set and multiply each number by the other numbers
    for(let i=0;i<nums1.length;i++){
        for(let j=i+1; j<nums1.length; j++){
            const res = nums1[i] * nums1[j];
            if(!nums1Dict[res]) nums1Dict[res] = 1;
            else nums1Dict[res] += 1;
        }
    }

    // Go over the second set and multiply each number by the other numbers
    for(let i=0;i<nums2.length;i++){
        for(let j=i+1; j<nums2.length; j++){
            const res = nums2[i] * nums2[j];
            if(!nums2Dict[res]) nums2Dict[res] = 1;
            else nums2Dict[res] += 1;
        }
    }

    let total = 0;

    // Go over the first set again and check if the item sq in nums2 multiplication dictionary
    nums1.forEach((item)=>{
        nums2Dict[item * item] ? total += nums2Dict[item * item]  : total;
    });

    // Go over the second set again and check if the item sq in nums1 multiplication dictionary    
    nums2.forEach((item)=>{
        nums1Dict[item * item] ? total += nums1Dict[item * item]  : total;
    });

    return total;
};
