use bisect to find the `i*i == j*k`

```
class Solution:
    def numTriplets(self, nums1: List[int], nums2: List[int]) -> int:
        s1,s2 = defaultdict(int),defaultdict(int)
        for s in nums1:
            s1[s] += 1
        for s in nums2:
            s2[s] += 1
        ans = 0
        def find(s1,s2):
            ans = 0
            for i in sorted(s1.keys()):
                s2sort = sorted(s2.keys())
                if i<s2sort[0]:
                    continue
                if i>s2sort[-1]:
                    break
                # print(sorted(s1.keys()),s2sort,i)
                for _j,j in enumerate(s2sort):
                    # print(sorted(s1.keys()),s2sort,i,j)
                    if j*s2sort[-1]<i*i:
                        continue
                    elif j>i:
                        break
                    elif (i*i)%j != 0:
                        continue
                    elif j == i:
                        ans += s1[i]*s2[j]*(s2[j]-1)//2
                        continue
                    else:
                        k = bisect_left(s2sort,i*i//j,_j+1)
                        while k<len(s2sort):
                            # print(s1.keys(),s2sort,i,j,s2sort[k],i*i-j*s2sort[k])
                            if j*s2sort[k] == i*i:
                                ans += s1[i]*s2[j]*s2[s2sort[k]]
                            k = bisect_left(s2sort,i*i//j,k+1)
            return ans
        t1,t2 = find(s1,s2),find(s2,s1)
        # print(t1,t2)
        return t1+t2

