#define ll long long

class Solution {
public:
ll help1(ll t,vector<int> &ar,unordered_map<ll,ll> &mp){
    ll n=ar.size(),ans=0;
    for(ll i=0;i<n;i++){
        if(t%ar[i]) continue;
        ll n1=ar[i],n2=t/ar[i];
        if(n2>n1) ans+=mp[n2];              //  choose all possible n2 for a given n1
        else if(n2==n1){
            ans+=(mp[n2]*(mp[n2]-1))/2;     //  choose any 2 numbers from mp[n2] numbers  
            break;
        }else break;                        //  break to prevent counting repeated pairs
    }
    return ans;
}

ll help2(vector<int>& ar1, vector<int>& ar2){
    ll n=ar1.size(),ans=0;
    unordered_map<ll,ll> mp;
    for(auto it:ar2) mp[it]++;
    for(ll i=0;i<n;i++) ans+=help1(1LL*ar1[i]*ar1[i],ar2,mp);
    return ans;
}

int numTriplets(vector<int>& ar1, vector<int>& ar2) {
    sort(ar1.begin(),ar1.end());
    sort(ar2.begin(),ar2.end());
    return help2(ar1,ar2)+help2(ar2,ar1);
}
};
