    #[test]
    fn test_sample_1() {
        let n1 = vec![7, 4];
        let n2 = vec![5, 2, 8, 9];
        assert_eq!(Solution::num_triplets(n1, n2), 1);
    }

    #[test]
    fn test_sample_2() {
        let n1 = vec![1, 1];
        let n2 = vec![1, 1, 1];
        assert_eq!(Solution::num_triplets(n1, n2), 9);
