    }

    #[test]
    fn test_sample_4() {
        let n1 = vec![4, 7, 9, 11, 23];
        let n2 = vec![3, 5, 1024, 12, 18];
        assert_eq!(Solution::num_triplets(n1, n2), 0);
    }
}
