class Solution {
public:
    #define ll long long
    int numTriplets(vector<int>& a, vector<int>& b) {
        sort(a.begin(),a.end());
        sort(b.begin(),b.end());
        return fun(a,b)+fun(b,a);
    }
    int fun(vector<int>& a, vector<int>& b){ //simple O(1) two sum (kinda) for every x in a.
        ll n=a.size(),m=b.size(),ans=0;
        for(auto &x:a){
            ll tar=x; tar=tar*((ll)x);
            int i=0,j=m-1;
            while(i<j){
                ll t=b[i]; t=t*((ll)b[j]);
                if(t<tar)
                    i++;
                else if(t>tar)
                    j--;
                else{
                    if(b[i]==b[j]){
                        ll len=j-i+1;
                        ans+=((len)*(len-1))/2;
                        break;
                    }
                    ll cnt1=0,cnt2=0,x=b[i],y=b[j];
                    while(b[i]==x)
                    {i++;cnt1++;}
                    while(b[j]==y)
                    {j--;cnt2++;}
                    ans+=cnt1*cnt2;
                }
            }
        }
        return ans;
    }
};
