  def numTriplets(num1: Array[Int], num2: Array[Int]): Int = {

    def f(dou: Array[Double], opp: Array[Double]): Int = {
      var count = 0
      dou.foreach(tar => {
        var s = 0
        var e = opp.length - 1
        while (s < e) {
          if (opp(s) * opp(e) < tar) {
            s += 1
          } else if (opp(s) * opp(e) > tar) {
            e -= 1
          } else {
            if (opp(s) == opp(e)) {
              var tot = e - s
              count += (1 + tot) * tot / 2
              s = e + 1
            } else {
              var a = s
              var b = e
              while (a < opp.length && opp(a) == opp(s)) a += 1
              while (b >= a && opp(e) == opp(b)) b -= 1
              count += (a - s) * (e - b)
              s = a
              e = b
            }
          }
        }
      })
      count
    }

    f(num1.map(Math.pow(_, 2)), num2.map(_.toDouble).sortWith(_ < _)) + f(
      num2.map(Math.pow(_, 2)),
      num1.map(_.toDouble).sortWith(_ < _)
    )

  }
