class Solution {
    func helper(_ nums1: [Int], _ nums2: [Int]) -> Int {
        let c2 = nums2.count
        var products = [Int: Int]()
        for i in 0..<(c2 - 1) {
            for j in (i + 1)..<c2 {
                products[nums2[i] * nums2[j], default: Int(0)] += 1
            }
        }
        return nums1.reduce(into: Int(0), { if let productCount = products[$1 * $1] { $0 += productCount } })
    }
    func numTriplets(_ nums1: [Int], _ nums2: [Int]) -> Int {
        return helper(nums1, nums2) + helper(nums2, nums1)
    }
}
