var numTriplets = function(numsA, numsB) {
    function addToMap(val, map) {
        map.set(val, 1 + (map.get(val) || 0))
    }

    const mapProductsA = new Map()
    const mapSquaresA = new Map()
    const mapProductsB = new Map()
    const mapSquaresB = new Map()


    for (const val of numsA.values())
        addToMap(val ** 2, mapSquaresA)
    for (const val of numsB.values())
        addToMap(val ** 2, mapSquaresB)

    for (let i = 0, len = numsA.length; i < len; i++) {
        const valI = numsA[i]
        for (let j = 1 + i; j < len; j++) {
            const valJ = numsA[j]
            const product = valI * valJ
            addToMap(product, mapProductsA)
        }
    }

    for (let i = 0, len = numsB.length; i < len; i++) {
        const valI = numsB[i]
        for (let j = 1 + i; j < len; j++) {
            const valJ = numsB[j]
            const product = valI * valJ
            addToMap(product, mapProductsB)
        }
    }


    let result = 0
    for (const [squareA, countA] of mapSquaresA.entries()) {
        const countB = mapProductsB.get(squareA) || 0
        let outcome = countA * countB
        result += outcome
    }
    for (const [squareB, countB] of mapSquaresB.entries()) {
        const countA = mapProductsA.get(squareB) || 0
        let outcome = countB * countA
        result += outcome
    }


    return result
};
