#define REP(i, n) for(int i = 0; i < n; i++)
#define REPP(i, n) for(int i = 1; i <= n; i++)
#define ALL(obj) (obj).begin(), (obj).end()

class Solution {
public:
    int numTriplets(vector<int>& nums1, vector<int>& nums2) {
        int ans = 0;
        sort(ALL(nums1)); sort(ALL(nums2));
        ans += helper(nums1, nums2);
        ans += helper(nums2, nums1);
        return ans;       
    }

    int helper(vector<int>& nums1, vector<int>& nums2){
        int n1 = nums1.size(), n2 = nums2.size();
        int res = 0;
        REP(i, n1){
            long target = (long)nums1[i] * (long)nums1[i];
            int l = 0, r = n2 - 1;
            while(l <= r){
                long now = (long)nums2[l] * (long)nums2[r];
                if(now < target){
                    l++;
                    continue;
                }else if(now > target){
                    r--;
                    continue;
                }
                int lcnt = 0, rcnt = 0;
                int lv = nums2[l], rv = nums2[r];
                while(l < n2 && nums2[l] == lv){
                    l++;
                    lcnt++;
                }
                while(r >= 0 && nums2[r] == rv){
                    r--;
                    rcnt++;
                }
                if(lv != rv) res += lcnt * rcnt;
                else res += (lcnt * (rcnt - 1)) / 2;
            }
        }
        return res;
    }
};
