         res += findTriplesHelper(nums1, nums2);
         res += findTriplesHelper(nums2, nums1);
         return res;
    }


    public int findTriplesHelper(int[] nums1, int[] nums2){
        Map<Integer,Integer> map = new HashMap<>();
        for(int ele: nums2){
            map.put(ele, map.getOrDefault(ele,0)+1);
        }


        int res = 0;

        for(int i=0;i<nums1.length;i++){
            long temp = (long)nums1[i]*(long)nums1[i];
            Set<Integer> set = new HashSet<>();
            for(Integer key: map.keySet()){
                if(set.contains(key)){
                    continue;
                }
                set.add(key);
                if(temp%key==0 && map.containsKey((int)(temp/key))){

                    if((int)(temp/key)!=key){
                       res += map.get(key) * map.get((int)(temp/key));
                       set.add((int)(temp/key));
                    }else{
                        res += map.get(key)*(map.get(key)-1)/2;
                    }
                }
            }
        }

        return res;
    }
}
