        for(int i=0;i<nums1.size()-1;i++){
            for(int j=i+1;j<nums1.size();j++)
                mp1[(long)nums1[i]*nums1[j]]++;
        }
        for(int i=0;i<nums2.size()-1;i++){
            for(int j=i+1;j<nums2.size();j++)
                mp2[(long)nums2[i]*nums2[j]]++;
