fun numTriplets(nums1: IntArray, nums2: IntArray): Int {
    return numTripletsAux(nums1, nums2) + numTripletsAux(nums2, nums1)
}

private fun numTripletsAux(nums1: IntArray, nums2: IntArray): Int {
    val squareCounts = mutableMapOf<Long, Int>()

    for (n in nums1) {
        val square = n.toLong() * n.toLong()
        squareCounts[square] = squareCounts.getOrDefault(square, 0) + 1
    }

    var count = 0
    for (i in nums2.indices) {
        for (j in i + 1 until nums2.size) {
            count += squareCounts.getOrDefault(nums2[i].toLong() * nums2[j].toLong(), 0)
        }
    }
    return count
}
