class Solution {
public:
    int lengthOfLastWord(string s) {
        stack<char>st;
        st.push('9');
        for(auto it: s){
            st.push(it);
        }
        int c =0;
        while(true){
            if(st.top()==' ' && c>0){
                break;
            }

            if(st.top()=='9'){
                break;
            }
            else if(st.top()!=' '){
                c++;
                st.pop();
            }

            else{
                st.pop();
            }
        }
        return c;
    }
};
