func lengthOfLastWord(s string) (res int) {
    i := len(s) - 1

    // Skip trailing space char if any
    for i >= 0 {
        if s[i] == ' ' {
            i--
            continue
        }
        break
    }

    // Search for end of word i.e space char
    for i >= 0 {
        if s[i] == ' ' {
            break
        }
        i--
        res++
    }

    return res
}
```

