            if(str.charAt(i) == ' ') {
                stk.push(i) ;
            }
        }
        if(stk.isEmpty()){
            return str.length() ;
        }
        int k = stk.pop() + 1;
        String $str = str.substring(k) ;

        return $str.length() ;
    }

