class Solution {
    public int threeSumClosest(int[] nums, int target) {
        //base case
        if (nums == null) return -1;

        Arrays.sort(nums);
        int n = nums.length;
        int ans = Integer.MAX_VALUE;
        int minDiff = Integer.MAX_VALUE;
        for(int i=0; i<n-2; i++){
            int sum = twoSum(nums, i, target, ans, minDiff);
            if(Math.abs(target - sum) == 0) return sum;
            if(Math.abs(target - sum) <= minDiff)
            {
                ans = sum;
                minDiff = Math.abs(target - sum);
            }
        }
        return ans;
    }

    private int twoSum(int[] nums, int rootIndex, int target, int summ, int minDiff){
        int left = rootIndex + 1;
        int right = nums.length - 1;
        while(left < right){
            int sum = nums[rootIndex] + nums[left] + nums[right];
            int diff = Math.abs(target - sum);
            if(diff <= minDiff) {
                minDiff = diff;
                summ = sum;
            }
            if(minDiff == 0)
                return sum;
            if(sum > target){
                right--;
            }
            else{
                left++;
            }
        }
        return summ;
    }
}
