class Solution {
    func getPermutation(_ n: Int, _ k: Int) -> String {

        var fac = [Int](repeating: 1, count: n + 1)
        for i in 1..<(n+1) { fac[i] = fac[i - 1] * i }
        fac.reverse()

        var k = k - 1

        var list = [Int](repeating: 0, count: n)
        for i in 1..<(n + 1) { list[i - 1] = i }

        var res = [Int]()

        for i in 1..<(n + 1) {
            var index = k / fac[i]
            res.append(list[index])
            list.remove(at: index)    
            k -= fac[i] * index
        }

        return res.compactMap { String($0) }.joined()
    }
}
```

