Here is the efficient solution(100% faster, O(N) time complexity), its a bit mathematical here we will find the required string by dividing all the permutations in to different parts according to the first element
eg s= "123" can be divided as -
123 132
213  231
312 321
```
class Solution {
public:
    int fact(int n) {
        int ans=1;
        for(int i=1; i<=n; i++) ans*=i;
        return ans;
    }

    void solve(int k, string s, string& ans) {    
        int total_size=0, part_size, n, temp;  
        while(s.size()>2) {
            n= s.size();
            total_size= fact(n);
            part_size= total_size/n;

            temp= (k-1)/part_size;

            if(k%part_size==0)
                k=part_size;
            else
                k= k%part_size;

            ans+= s[temp];
            s.erase(temp, 1);           
        }
        cout<<s;
    }

    string getPermutation(int n, int k) {
        string ans, s="";        
        for(int i=1; i<=n; i++) s+= (i+48);

        solve(k, s, ans);
        return ans;
    }
};

