class Solution {
public:
    void nextPermutation(vector<int>& nums) {
        int n = nums.size();
        if (n == 1)
            return;
        if (n == 2)
        {
            swap(nums[0],nums[1]);
            return;
        }
        int j = n-1;
        while(j > 0)
        {
            if (nums[j-1] < nums[j])
            {
                reverse(begin(nums)+j,end(nums));
                auto idx = upper_bound(begin(nums)+j,end(nums),nums[j-1]) - begin(nums);
                swap(nums[j-1],nums[idx]);
                return;
            }
            j--;
        }
        sort(begin(nums),end(nums));
        return;
    }
};

60. Permutation Sequence :- https://leetcode.com/problems/permutation-sequence/

class Solution {
    vector<int> fact;
    vector<string> digits;
    string ans;
public:
    string getPermutation(int n, int k) {
        fact = vector<int>(n);
        digits = vector<string>(n);
        ans = "";
        fact[0] = 1;
        for(int i = 1 ; i < n ; i++)
            fact[i] = fact[i-1] * i;
        for(int i = 1 ; i <= n ; i++)
            digits[i-1] = to_string(i);
        solve(n,k);
        return ans;
    }
private:
    void solve(int n, int k)
    {
        if (n == 1)
        {
            ans += digits[0];
            return;
        }
        int index = k/fact[n-1];
        if (k % fact[n-1] == 0)
            index--;
        ans = ans + digits[index];
        digits.erase(begin(digits)+index);
        k = k - fact[n-1] * index;
        n--;
        solve(n,k);
