class Solution {
public:
    string helper(int n, set<int> st, int k){
        // basecase
        if(n == st.size()){ return ""; }

        // transition
        int val = 1;
        for(int i = 1; i<n - st.size(); i++) val *= i;

        int pos = k/val + (k%val ? 1 : 0);

        string s;
        for(int i = 1; i<=n; i++){
            if(st.find(i) == st.end() && --pos == 0){
                st.insert(i);
                s = '0' + i;
                break;
            }
        }

        return s + helper(n, st, (k % val) ? (k % val) : val);
    }

    string getPermutation(int n, int k) {
        set<int> st;
        return helper(n, st, k);
    }
};
```

