public:
    string getPermutation(int n, int k) {
        int fact = 1;
        vector<int> nums;

        // calculate fact till n-1
        for(int i=1; i<n; i++){
            fact *= i;
            nums.push_back(i);
        }
        nums.push_back(n);

        k -= 1; // as we are using 0-based indexing

        string res = "";

        while(true){
            res += to_string(nums[k/fact]);
            nums.erase(nums.begin() + k/fact);

            if(nums.size() == 0) break; // no more elements left

            k %= fact;
            fact /= nums.size();
        }

        return res;
    }
};
