/*
Example:

n = 4 and k = 9

number of combination 

1234    2134    3124    4123
1243    2143    3142    4132
1324    2314    3214    4213
1342    2342    3241    4231
1423    2413    3412    4312
1432    2431    3421    4321

since we want the 9 the permutaion we have to do the permutaion 8 time since 1st combination
is considered 1
k = 8
ans = "";

value of i = 1; //initially
now how many columns needs to be skipped for first digit of ans is (n-i)!
so number of the value (smaller) to skip = k/(n-1)!
here 8/(4-i)! = 8/6 = 1; 
new k value = k%(n-i) = 8%(4-1)! = 2
ans = "2"

repeation the same process i = 2
2/(4-2)! = 2/1 = 2
ans = "23"
k = 0

i = 3
0/(4-3)! = 0
ans = "231"

i = 4
0/(4-4)! = 0
ans = "2314"

result = "2314"
*/



class Solution {
public:

	int factorial(int n){
		int ans = 1;
		for(int i = 2; i<=n; i++)ans *= i;

		return ans;
	}

	string getPermutation(int n, int k) {


		//factorial of n to get the total number of arrangement possible
		//with n numbers
		int ttlArgmtPsbl = factorial(n); //total arrangement possible for n digits


		//since we want to get the number we will se after k permutation 
		//ans we are counting the initial arrangement as 1
		k--;

		//to remove loop means the same arrangement is occuring more than once.
		k = k%ttlArgmtPsbl;

		//to skip the number after which we will get the number which is supposed to 
		//be there in that place.
		vector<bool> rec(n, true);


		//resulting string
		string ans = "";


		for(int i = n; i>=1; i--){

			//to get the column of the number after a position when a number is fixed
			//at that position.
			ttlArgmtPsbl = ttlArgmtPsbl/i; 

			//fixing the first element and figuring
			//out how many column (where the number less than it which are unoccupied are
			//at that position) have to be skipped (reffer to the example)
			//(+1 to get the the exact index)

			//smaller and available no. to skip
			int smlaAvlnumbertoskp = k/ttlArgmtPsbl + 1; 

			//to store the index number.
			int ix = 0;

			//to get the ixdex value by skipping the available smaller number.
			for(int j = 0; j<rec.size(); j++){
				if(!smlaAvlnumbertoskp){
					break;
				}
				if(rec[j]){
					smlaAvlnumbertoskp--;
					ix = j;
				}
			}
			//number at ix is to be added to ans string

			k = k%ttlArgmtPsbl; //reduce the width of the column

			//set the number at ix to unavailable.
			rec[ix] = false;

			//add number at ix to ans.
			ans += to_string(ix+1);
		}

		return ans;
	}
};

