class Solution {
private:
int calFact ( int n ){
    int ans = 1 ;
    for ( int i = 1 ; i < n ; i ++)
        ans *=  i;
    return ans;
}    

public:
    string getPermutation(int n, int k) {
        // this question is based on the nice concept of factorial 
        // do check it out again

        // first we will find the factorial of the given n
        // then we will try to find in which range of factorial does k - 1 lies 
        // we will pick that element by k / size of the set reduced to ( fact ( n - 1 )) 
        // then we will pick the element and add to our string and erase the element from string 
        // then we will change the value of k by k = k % fact 
        // and fact = fact ( n - 1) = fact / s.size() 

        vector<int> numbers;
        int fact = 1 ;

        for ( int i = 1 ; i <= n ; i ++) numbers.push_back(i);

        fact = calFact ( n );
        k = k - 1;
        string s = "";
        while ( 1 ){
            s += to_string(numbers[(int)(k / fact )]);
            numbers.erase( numbers.begin() + (int)(k / fact) );

            // if the numbers become empty we will stop
            if ( numbers.size() == 0)
               break;

            // new k value and fact value 
            k = k % fact ;
            fact = fact / numbers.size();
        }

        return s;
    }
};
