class Solution {
    public String getPermutation(int n, int k) {
        int kF = 1;
        List<Integer>allDigits = new ArrayList<>();
        for (int i=1;i<=n;i++){
            kF *=i;
            allDigits.add(i);
        }

        StringBuilder res = new StringBuilder();
        int ind = 0;
        while (allDigits.size()>0){
            kF/=allDigits.size();
            int div = k/kF;
            int mod = k%kF;
            ind = (mod>0?0:-1) + div;
            k=(mod==0)?kF:mod;
            res.append(allDigits.get(ind));
            allDigits.remove(ind);
        }
        return res.toString();
    }
}
