class Solution {
public:
    string getPermutation(int n, int k) {
        int fact=1;
        vector<int>num;
        for(int i=1;i<n;++i)
        {
            num.push_back(i);
            fact*=i;
        }
        num.push_back(n);
        string res="";
        k-=1;
        while(1)
        {
          res+=to_string(num[k/fact]);
          num.erase(num.begin()+k/fact);//after adding to res we will delete this char.
          if(size(num)==0)
            break;
           k%=fact;
           fact/=size(num);
        }
        return res;
    }
};
