    def getIntersectionNode(self, headA: ListNode, headB: ListNode) -> ListNode:
        seen = set()
        while headA != None:
            seen.add(headA)
            headA = headA.next

        while headB != None:
            if headB in seen:
                return headB
            headB = headB.next
        return None
