    while(currA) {
        ++lenA;
        currA = currA.next;
    }
    while(currB) {
        ++lenB;
        currB = currB.next;
    }

    // move the longer list steps equal to the difference between their lengths
    currA = headA;
    while(lenA > lenB) {
        currA = currA.next;
        --lenA;
    }
    currB = headB;
    while(lenA < lenB) {
        currB = currB.next;
        --lenB;
    }

    // move both pointer one step at a time until they meet at the intersection or both are equal to null
    // since from the start of this loop, both will have to move same distance until they reach the end
    while(currA !== currB) {
        currA = currA.next;
        currB = currB.next;   
    }
    return currA;
};
