var getIntersectionNode = function(headA, headB) {
    const map = new WeakMap();

    while (headA || headB) {
        if (headA) {
            if (map.has(headA)) {
                return headA;
            }

            map.set(headA, true);
            headA = headA.next;
        }

        if (headB) {
            if (map.has(headB)) {
                return headB;
            }

            map.set(headB, true);
            headB = headB.next;
        }
    }

    return null;
};
