    int minOperations(vector<string>& logs) {
         int ans = 0;
        for (string log : logs) {
            if (log == "../") { // go deeper
                ans--; 
                ans = max(ans, 0);
            } else if (log != "./") // one level up
			   ans++; 
        }
        return ans;
    }
};
