class Solution {
public:
    int minOperations(vector<string>& logs) {
        int count = 0;

        for (int i = 0; i < logs.size(); i++) {
            if (logs[i] == "./") {
                // Do nothing, stay in the same folder
            } else if (logs[i] == "../") {
                // Go up to the parent folder
                if (count > 0) {
                    count--;
                }
            } else {
                // Move to a child folder
                count++;
            }
        }

        return max(0, count);
    }
};

```

