public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
    //Keep track of customers who are waiting and customers who are on-board.
	int waitingCustomers = customers[0], totalOnBoard = 0;
    int result = -1, maxProfit = 0;
    for(int i=1; i<customers.length || waitingCustomers>0; i++){
         //In one round you can board max. 4 customers
		int newlyOnboarded = Math.min(4,waitingCustomers);
		//Substract newlyOnBoarded customer from waiting-list.
        waitingCustomers-=newlyOnboarded;
        totalOnBoard+=newlyOnboarded;

        int profit = totalOnBoard*boardingCost - i*runningCost;
		//If the profit is maximum, update the result.
        if(profit>maxProfit){
            maxProfit = profit;
            result = i;
        }
		//customers will be added to waiting customer count.
        if(i<customers.length) waitingCustomers +=customers[i];
    }
    return result;
}
