        while(idx < len || waiting > 0)
        {
            int val = idx < len ? customers[idx] : 0 ;
            if(idx < len && customers[idx] > 4)
            {
                waiting += (val - 4);
                val = 4;
            }

            if( val < 4 && waiting > 0)
            {
                int free = 4 - val;
                val += waiting >= free ? free : waiting;
                waiting = waiting >= free ? waiting - free : 0;
            }

            cost = cost +  ((boardingCost * val) - runningCost);

            if(max < cost)
            {
                max = cost;
                rotation = idx+ 1;
            }
            idx++;
        }        
        return rotation;
    }
