var minOperationsMaxProfit = function (customers, boardingCost, runningCost) {
  const n = customers.length
  let maxRotations = 0
  let waitingCustomers = 0
  let currentProfit = 0
  let maxProfit = 0

  for (let rotation = 1; rotation <= n || waitingCustomers > 0; rotation++) {
    if (rotation <= n) {
      waitingCustomers += customers[rotation - 1]
    }
    const boardedRiders = Math.min(waitingCustomers, 4)
    waitingCustomers -= boardedRiders
    currentProfit += boardedRiders * boardingCost - runningCost

    if (currentProfit > maxProfit) {
      maxProfit = currentProfit
      maxRotations = rotation
    }
  }

  return maxRotations || -1
}
