function minOperationsMaxProfit(customers: number[], boardingCost: number, runningCost: number): number {
  let profit = 0;
  let maxProfitRound = -1;
  let maxProfit = -Infinity;
  let round = 1
  while (true) {
    let toBoard = 0
    const maybeBoard = customers.shift()
    if (maybeBoard) {
      toBoard = Math.min(maybeBoard, 4)
